import React, { useEffect, useState } from "react";
import api from "../api";
import "./ClaimHistory.css";
const CLAIM_HISTORY_LIMIT = 10;
export default function ClaimHistory({ userId, refresh }) {
  const [history, setHistory] = useState([]);
  useEffect(() => {
    api.get(userId ? `/users/history/${userId}` : "/users/history").then(res =>
      setHistory(res.data.slice(-CLAIM_HISTORY_LIMIT).reverse())
    );
  }, [userId, refresh]);

  return (
    <div className="claim-history">
      <h4>Claim History</h4>
      <ul>
        {history.map((h, i) => (
          <li key={i}>{h.userId.name} claimed {h.points} points on{" "} {new Date(h.claimedAt).toLocaleString()}</li>
        ))}
      </ul>
    </div>
  );
}
