import React, { useState } from "react";
import LeaderboardTabs from "./components/LeaderboardTabs";
import LeaderboardPodium from "./components/LeaderboardPodium";
import LeaderboardTable from "./components/LeaderboardTable";
import AddUserForm from "./components/AddUserForm";
import ClaimButton from "./components/ClaimButton";
import ClaimHistory from "./components/ClaimHistory";
import SettlementTimer from "./components/SettlementTimer";
import "./App.css";

// Subtab options per main leaderboard tab, colors per your screenshots
const SUBTABS = {
  wealth: ["Daily", "Monthly"],
  live: ["Contribution", "Star tasks"],
  hourly: ["Hourly Live List", "Hourly Party List"],
  party: ["Weekly Contribution Ranking", "Weekly Charm Ranking"],
};

const TAB_COLORS = {
  wealth: "#FBE4C3",
  live: "#FFF4DB",
  hourly: "#EFDCF9",
  party: "#FFE7CC",
};

function App() {
  const [tab, setTab] = useState("wealth");
  const [subtab, setSubtab] = useState(SUBTABS["wealth"][1]); // default to Monthly/second
  const [selectedUser, setSelectedUser] = useState(null);
  const [refresh, setRefresh] = useState(false);

  // Example: Different settlement end times per leaderboard type
  const settlementEnds = {
    wealth: Date.now() + 14 * 24 * 3600 * 1000 + 1 * 3600 * 1000 + 45 * 60 * 1000 + 47 * 1000, // 14d 1h 45m 47s
    live: Date.now() + 2 * 24 * 3600 * 1000 + 1 * 3600 * 1000 + 45 * 60 * 1000 + 41 * 1000,   // 2d 1h 45m 41s
    hourly: Date.now() + 46 * 60 * 1000 + 34 * 1000, // 46m 34s
    party: Date.now() + 2 * 24 * 3600 * 1000 + 1 * 3600 * 1000 + 45 * 60 * 1000 + 27 * 1000, // 2d 1h 45m 27s
  };

  // Handle tab/subtab color switch
  const currentBg = TAB_COLORS[tab] || "#fdf9ef";

  // Handle tab switch and subtab reset
  const handleTabChange = (newTab) => {
    setTab(newTab);
    setSubtab(SUBTABS[newTab][1] || SUBTABS[newTab][0]);
    setSelectedUser(null); // Optional: clear user on tab change
  };

  return (
    <div className="lb-root" style={{ background: currentBg }}>
      {/* Top tab bar (Wealth, Live, Hourly, Party) */}
      <LeaderboardTabs active={tab} onChange={handleTabChange} />

      {/* Subtab bar (Monthly/Daily, Star tasks, etc.) */}
      <div className="lb-subtabs">
        {SUBTABS[tab].map((s) => (
          <button
            key={s}
            className={`lb-subtab-btn${subtab === s ? " active" : ""}`}
            onClick={() => setSubtab(s)}
          >{s}</button>
        ))}
        {/* Rewards Button at top-right, styled as your screens */}
        <button
          className="lb-reward-btn"
          style={{
            float: "right",
            background: "none",
            border: "none",
            marginLeft: "auto",
            position: "absolute",
            top: "12px",
            right: "10px"
          }}
          title="Rewards"
        >
          <img src="https://cdn-icons-png.flaticon.com/128/3531/3531755.png" alt="Rewards" height={38} style={{background:"#fffbe5",borderRadius:"50%",boxShadow:"0 2px 12px #ffdfb4"}}/>
        </button>
      </div>

      {/* Settlement Timer, above the podium */}
      <SettlementTimer endTime={settlementEnds[tab]} />

      {/* Podium -- shows top 3, themed per tab, click to select user */}
      <LeaderboardPodium
        tab={tab}
        refresh={refresh}
        onSelect={setSelectedUser}
      />

      {/* Add user and claim button, below the podium */}
      <div className="lb-controls">
        <AddUserForm onAdd={() => setRefresh((r) => !r)} />
        <ClaimButton userId={selectedUser} onClaim={() => setRefresh((r) => !r)} />
      </div>

      {/* Main table -- ranks 4+ */}
      <LeaderboardTable
        tab={tab}
        selected={selectedUser}
        onSelect={setSelectedUser}
        refresh={refresh}
        subtab={subtab}
      />

      {/* Claim History, filtered by selected user */}
      <ClaimHistory userId={selectedUser} refresh={refresh} />
    </div>
  );
}

export default App;
