import React, { useEffect, useState } from "react";
import api from "../api";
import "./Podium.css";

export default function LeaderboardPodium({ tab, refresh, onSelect }) {
  const [top, setTop] = useState([null, null, null]);

  useEffect(() => {
    api.get(`/users`).then(res => {
      setTop([res.data[0], res.data[1], res.data[2]]);
    });
  }, [tab, refresh]);

  return (
    <div className="podium">
      <div className="podium-card second" onClick={() => onSelect(top[1]?._id)}>
        <div className="podium-rank silver">2</div>
        <div className="podium-name">{top[1]?.name || "-"}</div>
        <div className="podium-points">{top[1]?.totalPoints ?? "-"}</div>
      </div>
      <div className="podium-card first" onClick={() => onSelect(top[0]?._id)}>
        <div className="podium-rank gold">1</div>
        <div className="podium-crown" />
        <div className="podium-name">{top[0]?.name || "-"}</div>
        <div className="podium-points">{top[0]?.totalPoints ?? "-"}</div>
      </div>
      <div className="podium-card third" onClick={() => onSelect(top[2]?._id)}>
        <div className="podium-rank bronze">3</div>
        <div className="podium-name">{top[2]?.name || "-"}</div>
        <div className="podium-points">{top[2]?.totalPoints ?? "-"}</div>
      </div>
    </div>
  );
}
