import React, { useEffect, useState } from 'react';
import api from '../api';

function ClaimHistory({ userId, refresh }) {
  const [history, setHistory] = useState([]);
  useEffect(() => {
    const url = userId ? `/users/history/${userId}` : '/users/history';
    api.get(url).then(res => setHistory(res.data.reverse()));
  }, [userId, refresh]);

  return (
    <div style={{ margin: '2rem 0' }}>
      <h4>Claim History</h4>
      <ul>
        {history.map((h, idx) => (
          <li key={idx}>
            {h.userId.name} claimed {h.points} points on {new Date(h.claimedAt).toLocaleString()}
          </li>
        ))}
      </ul>
    </div>
  );
}
export default ClaimHistory;
