import React, { useEffect, useState } from 'react';
import api from '../api';

function Leaderboard({ refresh }) {
  const [users, setUsers] = useState([]);
  useEffect(() => {
    api.get('/users').then(res => setUsers(res.data));
  }, [refresh]);

  return (
    <div style={{
      margin: '2rem 0', padding: '1rem',
      border: '1px solid #eee', borderRadius: 10, background: '#faf9ff'
    }}>
      <h3>Leaderboard</h3>
      <table style={{ width: '100%', textAlign: 'left' }}>
        <thead>
          <tr><th>Rank</th><th>Name</th><th>Total Points</th></tr>
        </thead>
        <tbody>
          {users.map((u, i) =>
            <tr key={u._id} style={{ background: i < 3 ? '#ffe7a2' : 'inherit' }}>
              <td>{i + 1}</td>
              <td>{u.name}</td>
              <td>{u.totalPoints}</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
export default Leaderboard;
